import React from 'react';
import { Col, Form, Row } from 'react-bootstrap';

function TalentItem({ talent, index, onChange, onRemove }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    onChange(index, { ...talent, [name]: value });
  };

  const handleCheckboxChange = (e) => {
    onChange(index, { ...talent, selected: e.target.checked });
  };

  return (
    <div className="talent-item mt-4">
      <Row className="align-items-center">
        <Col sm={1}>
          <Form.Check
            type="checkbox"
            checked={talent.selected}
            onChange={handleCheckboxChange}
          />
        </Col>
        <Col sm={4}>
          <Form.Control
            type="text"
            name="name"
            placeholder="Talent Name"
            value={talent.name}
            onChange={handleInputChange}
            disabled={!talent.selected}
          />
        </Col>
        <Col sm={3}>
          <Form.Control
            type="text"
            name="contractDuration"
            placeholder="Contract Duration"
            value={talent.contractDuration}
            onChange={handleInputChange}
            disabled={!talent.selected}
          />
        </Col>
        <Col sm={2}>
          <Form.Control
            type="text"
            name="billRate"
            placeholder="Bill Rate"
            value={talent.billRate}
            onChange={handleInputChange}
            disabled={!talent.selected}
          />
        </Col>
        <Col sm={2}>
          <Form.Control
            as="select"
            name="currency"
            value={talent.currency}
            onChange={handleInputChange}
            disabled={!talent.selected}
          >
            <option value="">Currency</option>
            <option value="USD">USD - Dollars ($)</option>
            <option value="EUR">EUR - Euros (€)</option>
            <option value="GBP">GBP - Pounds (£)</option>
          </Form.Control>
        </Col>
      </Row>
      <Row className="mt-2">
        <Col sm={6}>
          <Form.Control
            type="text"
            name="standardTimeBR"
            placeholder="Standard Time BR"
            value={talent.standardTimeBR}
            onChange={handleInputChange}
            disabled={!talent.selected}
          />
        </Col>
        <Col sm={6}>
          <Form.Control
            type="text"
            name="overTimeBR"
            placeholder="Over Time BR"
            value={talent.overTimeBR}
            onChange={handleInputChange}
            disabled={!talent.selected}
          />
        </Col>
      </Row>
    </div>
  );
}

export default TalentItem;
