import React, { useState } from 'react';
import { Form, Button, Row, Col } from 'react-bootstrap';
import TalentItem from './TalentItem';

function TalentDetails({ talents, onChange }) {
  const [newTalent, setNewTalent] = useState({
    name: '',
    contractDuration: '',
    billRate: '',
    currency: '',
    standardTimeBR: '',
    overTimeBR: '',
    isSelected: false,
  });

  const addTalent = () => {
    onChange([...talents, newTalent]);
    setNewTalent({
      name: '',
      contractDuration: '',
      billRate: '',
      currency: '',
      standardTimeBR: '',
      overTimeBR: '',
      isSelected: false,
    });
  };

  const handleTalentChange = (index, field, value) => {
    const updatedTalents = talents.map((talent, idx) => {
      if (index === idx) {
        return { ...talent, [field]: value };
      }
      return talent;
    });
    onChange(updatedTalents);
  };

  return (
    <div>
      <h4>Talent Detail</h4>
      {talents.map((talent, index) => (
        <TalentItem
          key={index}
          talent={talent}
          onChange={(field, value) => handleTalentChange(index, field, value)}
        />
      ))}
      <Row className="mt-3">
        <Col>
          <Form.Control
            type="text"
            placeholder="Talent Name"
            value={newTalent.name}
            onChange={(e) => setNewTalent({ ...newTalent, name: e.target.value })}
          />
        </Col>
        <Col>
          <Form.Control
            type="text"
            placeholder="Contract Duration"
            value={newTalent.contractDuration}
            onChange={(e) => setNewTalent({ ...newTalent, contractDuration: e.target.value })}
          />
        </Col>
        <Col>
          <Form.Control
            type="text"
            placeholder="Bill Rate"
            value={newTalent.billRate}
            onChange={(e) => setNewTalent({ ...newTalent, billRate: e.target.value })}
          />
        </Col>
        <Col>
          <Form.Control
            as="select"
            value={newTalent.currency}
            onChange={(e) => setNewTalent({ ...newTalent, currency: e.target.value })}
          >
            <option value="">Select Currency</option>
            <option value="USD">USD - Dollars ($)</option>
            {/* Add more currency options as needed */}
          </Form.Control>
        </Col>
        {/* Add other fields for Standard Time BR, Over Time BR, etc. */}
        <Col>
          <Button variant="secondary" onClick={addTalent}>+ Add Another</Button>
        </Col>
      </Row>
    </div>
  );
}

export default TalentDetails;
