import React from 'react';
import { Button, Row, Col, Form } from 'react-bootstrap';
import TalentItem from './TalentItem';

function TalentDetails({ talents, onChange }) {
  const handleTalentChange = (index, updatedTalent) => {
    const updatedTalents = [...talents];
    updatedTalents[index] = updatedTalent;
    onChange(updatedTalents);
  };

  const addTalent = () => {
    onChange([
      ...talents,
      {
        name: '',
        contractDuration: '',
        billRate: '',
        currency: '',
        standardTimeBR: '',
        overTimeBR: '',
        jobTitle: '',
        jobID: '',
      },
    ]);
  };

  const removeTalent = (index) => {
    const updatedTalents = talents.filter((_, i) => i !== index);
    onChange(updatedTalents);
  };

  return (
    <div className="talent-details mt-4">
      {talents.map((talent, index) => (
        <div key={index} className="mb-4">
          <div className="d-flex justify-content-between">
            <h5>{talent.jobTitle || `Job Title ${index + 1}`}</h5>
            <Button variant="danger" onClick={() => removeTalent(index)}>
              Remove
            </Button>
          </div>
          <Row className="mb-3">
            <Col sm={6}>
              <Form.Control
                type="text"
                name="jobTitle"
                placeholder="Job Title/REQ Name"
                value={talent.jobTitle}
                onChange={(e) => handleTalentChange(index, { ...talent, jobTitle: e.target.value })}
              />
            </Col>
            <Col sm={6}>
              <Form.Control
                type="text"
                name="jobID"
                placeholder="Job ID/REQ ID"
                value={talent.jobID}
                onChange={(e) => handleTalentChange(index, { ...talent, jobID: e.target.value })}
              />
            </Col>
          </Row>
          <TalentItem
            talent={talent}
            index={index}
            onChange={handleTalentChange}
          />
        </div>
      ))}
      <Button variant="secondary" onClick={addTalent} className="mt-3">
        + Add Another Talent
      </Button>
    </div>
  );
}

export default TalentDetails;
