import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import TalentDetails from './TalentDetails';
import './PurchaseOrderForm.css';

function PurchaseOrderForm() {
  const [formData, setFormData] = useState({
    clientName: '',
    poType: '',
    poNumber: '',
    receivedOn: null,
    receivedFromName: '',
    receivedFromEmail: '',
    poStartDate: null,
    poEndDate: null,
    budget: '',
    currency: '',
    talents: [
      {
        name: 'Monika Goyal Test',
        contractDuration: '',
        billRate: '',
        currency: 'USD',
        standardTimeBR: '',
        overTimeBR: '',
        selected: false,
        jobTitle: 'Application Development',
        jobID: 'OWNAI_234',
      },
      {
        name: 'Shaili Khatri',
        contractDuration: '',
        billRate: '',
        currency: 'USD',
        standardTimeBR: '',
        overTimeBR: '',
        selected: false,
        jobTitle: 'Business Administrator',
        jobID: 'CLK_1288O',
      },
    ],
  });

  const handleTalentDetailsChange = (updatedTalents) => {
    setFormData({
      ...formData,
      talents: updatedTalents,
    });
  };

  const handleReset = () => {
    // Reset the form to initial state
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <Form onSubmit={handleSubmit} className="purchase-order-form">
      {/* Other form fields */}
      <TalentDetails
        talents={formData.talents}
        onChange={handleTalentDetailsChange}
      />
      <div className="d-flex justify-content-end mt-4">
        <Button variant="secondary" onClick={handleReset} className="me-3">
          Reset
        </Button>
        <Button type="submit" variant="primary">
          Save
        </Button>
      </div>
    </Form>
  );
}

export default PurchaseOrderForm;
