import React from 'react';
import { Form, Col, Row } from 'react-bootstrap';

function PurchaseOrderDetails({ formData, handleInputChange, handleDateChange, errors }) {
  return (
    <div className="purchase-order-details">
      <Row>
        <Col sm={6}>
          <Form.Group controlId="clientName">
            <Form.Label>Client Name</Form.Label>
            <Form.Control
              type="text"
              name="clientName"
              value={formData.clientName}
              onChange={handleInputChange}
              isInvalid={!!errors.clientName}
            />
            <Form.Control.Feedback type="invalid">{errors.clientName}</Form.Control.Feedback>
          </Form.Group>
        </Col>
        <Col sm={6}>
          <Form.Group controlId="poType">
            <Form.Label>PO Type</Form.Label>
            <Form.Control
              type="text"
              name="poType"
              value={formData.poType}
              onChange={handleInputChange}
              isInvalid={!!errors.poType}
            />
            <Form.Control.Feedback type="invalid">{errors.poType}</Form.Control.Feedback>
          </Form.Group>
        </Col>
      </Row>
      <Row>
        <Col sm={6}>
          <Form.Group controlId="poNumber">
            <Form.Label>PO Number</Form.Label>
            <Form.Control
              type="text"
              name="poNumber"
              value={formData.poNumber}
              onChange={handleInputChange}
              isInvalid={!!errors.poNumber}
            />
            <Form.Control.Feedback type="invalid">{errors.poNumber}</Form.Control.Feedback>
          </Form.Group>
        </Col>
        <Col sm={6}>
          <Form.Group controlId="receivedFromName">
            <Form.Label>Received From Name</Form.Label>
            <Form.Control
              type="text"
              name="receivedFromName"
              value={formData.receivedFromName}
              onChange={handleInputChange}
              isInvalid={!!errors.receivedFromName}
            />
            <Form.Control.Feedback type="invalid">{errors.receivedFromName}</Form.Control.Feedback>
          </Form.Group>
        </Col>
      </Row>
      {/* Add more fields as necessary */}
    </div>
  );
}

export default PurchaseOrderDetails;
