import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import 'react-datepicker/dist/react-datepicker.css';
import TalentDetails from './TalentDetails';

function PurchaseOrderForm() {
  const [formData, setFormData] = useState({
    clientName: '',
    poType: '',
    poNumber: '',
    receivedOn: null,
    receivedFromName: '',
    receivedFromEmail: '',
    poStartDate: null,
    poEndDate: null,
    budget: '',
    currency: '',
    talents: [
      {
        name: 'Monika Goyal Test',
        contractDuration: '',
        billRate: '',
        currency: 'USD',
        standardTimeBR: '',
        overTimeBR: '',
        selected: false,
      },
      {
        name: 'Shaili Khatri',
        contractDuration: '',
        billRate: '',
        currency: 'USD',
        standardTimeBR: '',
        overTimeBR: '',
        selected: false,
      },
    ],
  });

  // Define errors state
  const [errors, setErrors] = useState({});

  const handleTalentDetailsChange = (updatedTalents) => {
    setFormData({
      ...formData,
      talents: updatedTalents,
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleDateChange = (date, fieldName) => {
    setFormData({
      ...formData,
      [fieldName]: date,
    });
  };

  const validateForm = () => {
    let formErrors = {};

    // Add form validation logic...

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log(formData);
    }
  };

  const handleReset = () => {
    setFormData({
      clientName: '',
      poType: '',
      poNumber: '',
      receivedOn: null,
      receivedFromName: '',
      receivedFromEmail: '',
      poStartDate: null,
      poEndDate: null,
      budget: '',
      currency: '',
      talents: [
        {
          name: 'Monika Goyal Test',
          contractDuration: '',
          billRate: '',
          currency: 'USD',
          standardTimeBR: '',
          overTimeBR: '',
          selected: false,
        },
        {
          name: 'Shaili Khatri',
          contractDuration: '',
          billRate: '',
          currency: 'USD',
          standardTimeBR: '',
          overTimeBR: '',
          selected: false,
        },
      ],
    });
    setErrors({});
  };

  return (
    <Form onSubmit={handleSubmit}>
      {/* Your other form fields... */}

      {/* Talent Details Section */}
      <TalentDetails
        talents={formData.talents}
        poType={formData.poType}
        onChange={handleTalentDetailsChange}
      />

      {/* Form Buttons */}
      <div className="d-flex justify-content-end mt-4">
        <Button variant="secondary" onClick={handleReset} className="me-3">
          Reset
        </Button>
        <Button type="submit" variant="primary">
          Save
        </Button>
      </div>
    </Form> 
  );
}

export default PurchaseOrderForm;
