import React, { useState } from 'react';

function TalentDetails({ talents, poType, onChange }) {
  const [localTalents, setLocalTalents] = useState(talents);

  const handleTalentChange = (index, event) => {
    const updatedTalents = [...localTalents];
    updatedTalents[index] = {
      ...updatedTalents[index],
      [event.target.name]: event.target.value,
    };
    setLocalTalents(updatedTalents);
    onChange(updatedTalents);
  };

  const addTalent = () => {
    const newTalent = { talentName: '', talentEmail: '' };
    setLocalTalents([...localTalents, newTalent]);
    onChange([...localTalents, newTalent]);
  };

  return (
    <div>
      <h5>Talent Details</h5>
      {localTalents.map((talent, index) => (
        <div key={index} className="form-group">
          <label>Talent Name</label>
          <input
            type="text"
            className="form-control"
            name="talentName"
            value={talent.talentName}
            onChange={(e) => handleTalentChange(index, e)}
          />
          <label>Talent Email</label>
          <input
            type="email"
            className="form-control"
            name="talentEmail"
            value={talent.talentEmail}
            onChange={(e) => handleTalentChange(index, e)}
          />
        </div>
      ))}
      {poType === 'Group PO' && (
        <button type="button" className="btn btn-info mt-2" onClick={addTalent}>
          Add Another Talent
        </button>
      )}
    </div>
  );
}

export default TalentDetails;
