import React, { useState } from 'react';
import { Button, Col, Form, Row } from 'react-bootstrap';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import ClientDropdown from './ClientDropdown';
import TalentDetails from './TalentDetails';

function PurchaseOrderForm() {
  const [formData, setFormData] = useState({
    clientName: '',
    poType: '',
    poNumber: '',
    receivedOn: null,
    receivedFromName: '',
    receivedFromEmail: '',
    poStartDate: null,
    poEndDate: null,
    budget: '',
    currency: '',
    talents: [],
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleDateChange = (date, fieldName) => {
    setFormData({
      ...formData,
      [fieldName]: date,
    });
  };

  const validateForm = () => {
    let formErrors = {};

    if (!formData.clientName) formErrors.clientName = 'Client name is required.';
    if (!formData.poType) formErrors.poType = 'Purchase Order type is required.';
    if (!formData.poNumber) formErrors.poNumber = 'Purchase Order number is required.';
    if (!formData.receivedOn) formErrors.receivedOn = 'Received date is required.';
    if (!formData.receivedFromName) formErrors.receivedFromName = 'Name is required.';
    if (!formData.receivedFromEmail) formErrors.receivedFromEmail = 'Email is required.';
    if (!formData.poStartDate) formErrors.poStartDate = 'PO start date is required.';
    if (!formData.poEndDate) formErrors.poEndDate = 'PO end date is required.';
    if (!formData.budget) formErrors.budget = 'Budget is required.';

    setErrors(formErrors);

    return Object.keys(formErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log(formData);
    }
  };

  const handleReset = () => {
    setFormData({
      clientName: '',
      poType: '',
      poNumber: '',
      receivedOn: null,
      receivedFromName: '',
      receivedFromEmail: '',
      poStartDate: null,
      poEndDate: null,
      budget: '',
      currency: '',
      talents: [],
    });
    setErrors({});
  };

  const handleTalentDetailsChange = (updatedTalents) => {
    setFormData({
      ...formData,
      talents: updatedTalents,
    });
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Row className="mb-3">
        <Col>
          <Form.Group controlId="clientName">
            <Form.Label>Client Name</Form.Label>
            <ClientDropdown value={formData.clientName} onChange={handleChange} />
            {errors.clientName && <div className="text-danger">{errors.clientName}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="poType">
            <Form.Label>Purchase Order Type</Form.Label>
            <Form.Control as="select" name="poType" value={formData.poType} onChange={handleChange}>
              <option value="">Select</option>
              <option value="Group PO">Group PO</option>
              <option value="Individual PO">Individual PO</option>
            </Form.Control>
            {errors.poType && <div className="text-danger">{errors.poType}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="poNumber">
            <Form.Label>Purchase Order No.</Form.Label>
            <Form.Control
              type="text"
              name="poNumber"
              value={formData.poNumber}
              onChange={handleChange}
            />
            {errors.poNumber && <div className="text-danger">{errors.poNumber}</div>}
          </Form.Group>
        </Col>
      </Row>

      {/* Additional Form Fields */}
      <Row className="mb-3">
        <Col>
          <Form.Group controlId="receivedFromName">
            <Form.Label>Received From - Name</Form.Label>
            <Form.Control
              type="text"
              name="receivedFromName"
              value={formData.receivedFromName}
              onChange={handleChange}
            />
            {errors.receivedFromName && <div className="text-danger">{errors.receivedFromName}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="receivedFromEmail">
            <Form.Label>Received From - Email ID</Form.Label>
            <Form.Control
              type="email"
              name="receivedFromEmail"
              value={formData.receivedFromEmail}
              onChange={handleChange}
            />
            {errors.receivedFromEmail && <div className="text-danger">{errors.receivedFromEmail}</div>}
          </Form.Group>
        </Col>
      </Row>

      {/* Date Pickers */}
      <Row className="mb-3">
        <Col>
          <Form.Group controlId="receivedOn">
            <Form.Label>Received On</Form.Label>
            <DatePicker
              selected={formData.receivedOn}
              onChange={(date) => handleDateChange(date, 'receivedOn')}
              className="form-control"
            />
            {errors.receivedOn && <div className="text-danger">{errors.receivedOn}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="poStartDate">
            <Form.Label>PO Start Date</Form.Label>
            <DatePicker
              selected={formData.poStartDate}
              onChange={(date) => handleDateChange(date, 'poStartDate')}
              className="form-control"
            />
            {errors.poStartDate && <div className="text-danger">{errors.poStartDate}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="poEndDate">
            <Form.Label>PO End Date</Form.Label>
            <DatePicker
              selected={formData.poEndDate}
              onChange={(date) => handleDateChange(date, 'poEndDate')}
              className="form-control"
              minDate={formData.poStartDate}
            />
            {errors.poEndDate && <div className="text-danger">{errors.poEndDate}</div>}
          </Form.Group>
        </Col>
      </Row>

      {/* Budget and Currency */}
      <Row className="mb-3">
        <Col>
          <Form.Group controlId="budget">
            <Form.Label>Budget</Form.Label>
            <Form.Control
              type="number"
              name="budget"
              value={formData.budget}
              onChange={handleChange}
              max="99999"
            />
            {errors.budget && <div className="text-danger">{errors.budget}</div>}
          </Form.Group>
        </Col>
        <Col>
          <Form.Group controlId="currency">
            <Form.Label>Currency</Form.Label>
            <Form.Control as="select" name="currency" value={formData.currency} onChange={handleChange}>
              <option value="">Select Currency</option>
              <option value="USD">USD - Dollars ($)</option>
              {/* Add more currencies as needed */}
            </Form.Control>
          </Form.Group>
        </Col>
      </Row>

      {/* Talent Details Component */}
      <TalentDetails talents={formData.talents} poType={formData.poType} onChange={handleTalentDetailsChange} />

      {/* Form Buttons */}
      <div className="d-flex justify-content-end mt-4">
        <Button variant="secondary" onClick={handleReset} className="me-3">
          Reset
        </Button>
        <Button type="submit" variant="primary">
          Save
        </Button>
      </div>
    </Form>
  );
}

export default PurchaseOrderForm;
